import React, { useEffect, useState } from 'react';
import './ManageEstimate.css';
import Navbar from './Navbar';
import Footer from './Footer';

const ManageEstimate = () => {
  const [estimates, setEstimates] = useState([]);
  const [groups, setGroups] = useState([]);
  const [brands, setBrands] = useState([]);
  const [chains, setChains] = useState([]);
  const [zones, setZones] = useState([]);

  const [selectedGroup, setSelectedGroup] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');
  const [service, setService] = useState('');
  const [quantity, setQuantity] = useState('');
  const [costPerUnit, setCostPerUnit] = useState('');
  const [deliveryDate, setDeliveryDate] = useState('');
  const [deliveryDetails, setDeliveryDetails] = useState('');

  useEffect(() => {
    fetch('http://localhost:8080/estimate/all')
      .then((response) => response.json())
      .then((data) => setEstimates(data))
      .catch((error) => console.error('Error fetching estimates:', error));

    fetch('http://localhost:8080/groups/allgroups')
      .then((res) => res.json())
      .then((data) => setGroups(data))
      .catch((err) => console.error('Groups Error:', err));

    fetch('http://localhost:8080/brands/allbrand')
      .then((res) => res.json())
      .then((data) => setBrands(data))
      .catch((err) => console.error('Brands Error:', err));

    fetch('http://localhost:8080/chains/allcompany')
      .then((res) => res.json())
      .then((data) => setChains(data))
      .catch((err) => console.error('Chains Error:', err));

    fetch('http://localhost:8080/zones/allzones')
      .then((res) => res.json())
      .then((data) => setZones(data))
      .catch((err) => console.error('Zones Error:', err));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const estimateData = {
      groupName: selectedGroup,
      chainId: chains.find((chain) => chain.chain_name === selectedGroup)?.chain_id,
      brandName: selectedBrand,
      zoneName: zones.find((zone) => zone.zone_name === selectedGroup)?.zone_name,
      service,
      quantity,
      costPerUnit,
      deliveryDate,
      deliveryDetails,
    };

    fetch('http://localhost:8080/estimate/add', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(estimateData),
    })
      .then((response) => response.text())
      .then((data) => {
        alert('Estimate Created: ' + data);
        // Optionally, fetch the updated estimates here if needed
      })
      .catch((error) => console.error('Error creating estimate:', error));
  };

  const filteredEstimates = estimates.filter((estimate) => {
    const matchGroup = selectedGroup ? estimate.chain.group.group_name === selectedGroup : true;
    const matchBrand = selectedBrand ? estimate.brandName === selectedBrand : true;
    return matchGroup && matchBrand;
  });

  return (
    <div>
      <Navbar />
      <div className="container py-4">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="section-header">Estimate Management</h2>
        </div>

        <div className="row mb-4">
          <div className="col-md-4">
            <div className="neon-card">
              <h5>Total Estimate</h5>
              <h2>{filteredEstimates.length}</h2>
            </div>
          </div>
          <div className="col-md-4">
            <select
              className="form-select neon-select"
              aria-label="Filter by Group"
              value={selectedGroup}
              onChange={(e) => setSelectedGroup(e.target.value)}
            >
              <option value="" disabled>Filter by Group</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select neon-select"
              aria-label="Filter by Brand"
              value={selectedBrand}
              onChange={(e) => setSelectedBrand(e.target.value)}
            >
              <option value="" disabled>Filter by Brand</option>
              {brands.map((brand, index) => (
                <option key={index} value={brand}>{brand}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="d-flex justify-content-end mb-3">
          <div
            className="create-estimate-icon"
            data-bs-toggle="offcanvas"
            data-bs-target="#createPanel"
            role="button"
          >
            <i className="bi bi-plus-circle me-2"></i>
            Create Estimate
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped align-middle">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group</th>
                <th>Chain ID</th>
                <th>Brand</th>
                <th>Zone</th>
                <th>Service Details</th>
                <th>Total Units</th>
                <th>Price Per Unit</th>
                <th>Total</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {filteredEstimates.map((estimate, index) => (
                <tr key={estimate.estimateId}>
                  <td>{index + 1}</td>
                  <td>{estimate.chain.group.group_name}</td>
                  <td>{estimate.chain.chain_id}</td>
                  <td>{estimate.brandName}</td>
                  <td>{estimate.zoneName}</td>
                  <td>{estimate.service}</td>
                  <td>{estimate.quantity}</td>
                  <td>{estimate.costPerUnit}</td>
                  <td>{estimate.totalCost}</td>
                  <td className="table-action">
                    <i className="bi bi-pencil-square" data-bs-toggle="offcanvas" data-bs-target="#editPanel"></i>
                  </td>
                  <td className="table-action">
                    <i className="bi bi-trash"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Create Estimate Panel */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="createPanel">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Create Estimate</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form onSubmit={handleSubmit}>
            <label>Select Group:</label>
            <select
              className="form-select"
              value={selectedGroup}
              onChange={(e) => setSelectedGroup(e.target.value)}
            >
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>

            <label>Select Chain ID or Company Name:</label>
            <select
              className="form-select"
              value={chains.find((chain) => chain.chain_name === selectedGroup)?.chain_id}
              onChange={(e) => setSelectedBrand(e.target.value)}
            >
              {chains.map((chain, index) => (
                <option key={index} value={chain.chain_id}>{chain.chain_name}</option>
              ))}
            </select>

            <label>Select Brand:</label>
            <select
              className="form-select"
              value={selectedBrand}
              onChange={(e) => setSelectedBrand(e.target.value)}
            >
              {brands.map((brand, index) => (
                <option key={index} value={brand}>{brand}</option>
              ))}
            </select>

            <label>Select Zone:</label>
            <select
              className="form-select"
              value={zones.find((zone) => zone.zone_name === selectedGroup)?.zone_id}
              onChange={(e) => setSelectedBrand(e.target.value)}
            >
              {zones.map((zone, index) => (
                <option key={index} value={zone.zone_id}>{zone.zone_name}</option>
              ))}
            </select>

            <label>Service Provided:</label>
            <input
              type="text"
              className="form-control"
              value={service}
              onChange={(e) => setService(e.target.value)}
            />

            <label>Total Quantity:</label>
            <input
              type="number"
              className="form-control"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
            />

            <label>Cost Per Quantity:</label>
            <input
              type="number"
              className="form-control"
              value={costPerUnit}
              onChange={(e) => setCostPerUnit(e.target.value)}
            />

            <label>Estimated Amount in Rs:</label>
            <input
              type="number"
              className="form-control"
              placeholder="Disabled"
              disabled
            />

            <label>Expected Delivery Date:</label>
            <input
              type="date"
              className="form-control"
              value={deliveryDate}
              onChange={(e) => setDeliveryDate(e.target.value)}
            />

            <label>Other Delivery Details:</label>
            <textarea
              className="form-control"
              rows="3"
              value={deliveryDetails}
              onChange={(e) => setDeliveryDetails(e.target.value)}
            ></textarea>

            <button type="submit" className="btn glow-btn mt-3 w-100">Save Estimate</button>
          </form>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ManageEstimate;
