import React, { useState } from 'react';
import './ForgotPassword.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ForgotPassword = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Step 1: Validate user with /fp API
      const response = await axios.post(`http://localhost:8080/fp`, null, {
        params: {
          fullName: fullName,
          email: email,
        }
      });

      const data = response.data;

      // Step 2: Handle error if user not found
      if (
        Array.isArray(data) &&
        data.length === 1 &&
        data[0].message &&
        (data[0].message.includes("User not found") || data[0].message.includes("incorrect"))
      ) {
        toast.error(data[0].message, {
          position: "bottom-right",
        });
      } else {
        // Step 3: Save user data in local storage
        localStorage.setItem('forgotPasswordData', JSON.stringify(data[0]));

        // Step 4: Send mail using /sendmail API
        await axios.get(`http://localhost:8080/sendmail`, {
          params: {
            fullName: data[0].fullName,
            email: data[0].email,
          },
        });

        toast.success("Email sent successfully!", {
          position: "bottom-right",
        });
      }
    } catch (error) {
        toast.error("Something went wrong: " + JSON.stringify(error), {
          position: "bottom-right",
        });
        console.error("Error:", error);
      }
      
  };

  return (
    <div className='all'>
      <div className="form-card">
        <h2><i className="fas fa-envelope-open-text"></i> Send Email</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3 input-group">
            <span className="input-group-text"><i className="fas fa-user"></i></span>
            <input
              type="text"
              className="form-control"
              placeholder="Full Name"
              required
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
            />
          </div>
          <div className="mb-4 input-group">
            <span className="input-group-text"><i className="fas fa-envelope"></i></span>
            <input
              type="email"
              className="form-control"
              placeholder="Email Address"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-send">
            <i className="fas fa-paper-plane"></i> Send Email
          </button>
        </form>
        <div className="go-back mt-3">
          <Link to="/"><i className="fas fa-arrow-left"></i> Go Back</Link>
        </div>
      </div>

      <ToastContainer />
    </div>
  );
};

export default ForgotPassword;
