import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ManageGroup.css';
import Navbar from './Navbar';
import Footer from './Footer';

const ManageGroup = () => {
  const [groups, setGroups] = useState([]);
  const [newGroupName, setNewGroupName] = useState('');

  // Fetch all groups
  const fetchGroups = async () => {
    try {
      const response = await axios.get('http://localhost:8080/groups/all');
      setGroups(response.data);
    } catch (error) {
        console.error('Error adding group:', error.response?.data || error.message || error);
      }
      
  };

  // Add new group
  const handleAddGroup = async () => {
    if (!newGroupName.trim()) return;

    try {
      await axios.post('http://localhost:8080/groups/addgroup', {
        groupname: newGroupName,
      });
      setNewGroupName('');
      fetchGroups(); // Refresh group list
    } catch (error) {
      console.error('Error adding group:', error);
    }
  };

  useEffect(() => {
    fetchGroups();
  }, []);

  return (
    <div className='MG'>
      <Navbar />
      
      <div className="total-groups">Total Groups: {groups.length}</div>

      <div className="add-group-container">
        <h2 className="add-group-title">Add a New Group</h2>
        <input
          type="text"
          className="add-group-input"
          placeholder="Enter group name"
          value={newGroupName}
          onChange={(e) => setNewGroupName(e.target.value)}
        />
        <button className="add-group-btn" onClick={handleAddGroup}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      <div className="container">
        <table className="table table-dark table-bordered">
          <thead>
            <tr>
              <th>#</th>
              <th>Group Name</th>
              <th>Date Created</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {groups.map((group, index) => (
              <tr key={group.id || index}>
                <td>{index + 1}</td>
                <td>{group.groupname}</td>
                <td>{group.created_at?.split('T')[0] || 'N/A'}</td>
                <td className="action-icons">
                  <i className="fas fa-pen-to-square"></i>
                  <i className="fas fa-trash"></i>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Footer />
    </div>
  );
};

export default ManageGroup;
