import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import './ManageZone.css';

const ManageZone = () => {
  const [zones, setZones] = useState([]);
  const [brands, setBrands] = useState([]);
  const [selectedZone, setSelectedZone] = useState(null); // Add state for selected zone

  useEffect(() => {
    fetch('http://localhost:8080/zones/all')
      .then((res) => res.json())
      .then((data) => setZones(data))
      .catch((err) => console.error('Failed to fetch zones:', err));
  }, []);

  useEffect(() => {
    fetch('http://localhost:8080/brands/allbrand')
      .then((res) => res.json())
      .then((data) => setBrands(data))
      .catch((err) => console.error('Failed to fetch brands:', err));
  }, []);

  // Open Edit Sidebar with selected zone data
  const handleEditClick = (zone) => {
    setSelectedZone(zone); // Set the selected zone for editing
    const bootstrapModal = new window.bootstrap.Offcanvas(document.getElementById('editZoneSidebar'));
    bootstrapModal.show();
  };

  // Handle the update of the zone
  const handleUpdateZone = (e) => {
    e.preventDefault();

    const zoneName = document.getElementById('editZoneName').value;
    const brand = document.getElementById('editBrandSelect').value;

    fetch('http://localhost:8080/zones/update', {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        zone_id: selectedZone.zone_id, // Pass the zone_id of the selected zone
        zone_name: zoneName,
        brand: brand,
      }),
    })
      .then((res) => res.text())
      .then((data) => {
        alert(data); // Show response in alert
        // Close the sidebar
        const bootstrapModal = new window.bootstrap.Offcanvas(document.getElementById('editZoneSidebar'));
        bootstrapModal.hide();
        // Refresh the page to load the updated zone
        window.location.reload();
      })
      .catch((err) => console.error('Failed to update zone:', err));
  };

  return (
    <>
      <Navbar />

      <div className="container py-5">
        <h2 className="neon-text mb-4">Manage Zone Section</h2>

        <table className="table table-dark table-bordered table-hover align-middle">
          <thead>
            <tr>
              <th>Sr.No</th>
              <th>Zone</th>
              <th>Brand</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {zones.map((zone, index) => (
              <tr key={zone.zone_id}>
                <td>{index + 1}</td>
                <td>{zone.zone_name}</td>
                <td>{zone.brand?.brand_name}</td>
                <td>
                  <i
                    className="bi bi-pencil-square me-2"
                    style={{ cursor: 'pointer' }}
                    onClick={() => handleEditClick(zone)} // Open the sidebar with the selected zone
                  ></i>
                  <i className="bi bi-trash-fill" style={{ cursor: 'pointer' }}></i>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Edit Zone Sidebar */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="editZoneSidebar">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Edit Zone</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form onSubmit={handleUpdateZone}>
            <div className="mb-3">
              <label htmlFor="editZoneName" className="form-label">Zone Name</label>
              <input
                type="text"
                className="form-control"
                id="editZoneName"
                placeholder="Edit zone name"
                required
                defaultValue={selectedZone?.zone_name} // Prefill zone name
              />
            </div>
            <div className="mb-3">
              <label htmlFor="editBrandSelect" className="form-label">Brand</label>
              <select
                className="form-select"
                id="editBrandSelect"
                defaultValue={selectedZone?.brand?.brand_name} // Prefill selected brand
              >
                <option disabled value="">Select Brand</option>
                {brands.map((brand, index) => (
                  <option key={index} value={brand}>{brand}</option>
                ))}
              </select>
            </div>
            <button className="btn btn-edit w-100">Update Zone</button>
          </form>
        </div>
      </div>

      <Footer />
    </>
  );
};

export default ManageZone;
