import React, { useState } from 'react';
import './ForgotPassword.css';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ForgotPassword = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // 1. Validate username & email
      const response = await axios.post('http://localhost:8080/fp', {
        fullName: fullName,
        email: email,
      });

      const data = response.data;

      if (data && data.password) {
        toast.success("User validated successfully!", {
          position: "bottom-right",
        });

        localStorage.setItem('forgotPasswordData', JSON.stringify(data));

        // 2. Just call sendmail after validation (without params)
        await axios.get('http://localhost:8080/sendmail');
        toast.success("Email sent successfully!", {
          position: "bottom-right",
        });

      } else {
        toast.error("User not found or something went wrong", {
          position: "bottom-right",
        });
      }

    } catch (error) {
      toast.error("Error during password recovery. Please try again later.", {
        position: "bottom-right",
      });
    }
  };

  return (
    <div className='all'>
      <div className="form-card">
        <h2><i className="fas fa-envelope-open-text"></i> Send Email</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3 input-group">
            <span className="input-group-text"><i className="fas fa-user"></i></span>
            <input
              type="text"
              className="form-control"
              placeholder="Full Name"
              required
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
            />
          </div>
          <div className="mb-4 input-group">
            <span className="input-group-text"><i className="fas fa-envelope"></i></span>
            <input
              type="email"
              className="form-control"
              placeholder="Email Address"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-send">
            <i className="fas fa-paper-plane"></i> Send Email
          </button>
        </form>
        <div className="go-back mt-3">
          <Link to="/"><i className="fas fa-arrow-left"></i> Go Back</Link>
        </div>
      </div>

      <ToastContainer />
    </div>
  );
};

export default ForgotPassword;
