import React, { useEffect, useState } from 'react';
import './ManageEstimate.css';
import Navbar from './Navbar';
import Footer from './Footer';

const ManageEstimate = () => {
  const [groups, setGroups] = useState([]);
  const [brands, setBrands] = useState([]);
  const [chains, setChains] = useState([]);
  const [zones, setZones] = useState([]);
  const [estimates, setEstimates] = useState([]); // State to store fetched estimates

  // Fetch data from APIs on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const groupResponse = await fetch('http://localhost:8080/groups/allgroups');
        const groupData = await groupResponse.json();
        setGroups(groupData);

        const brandResponse = await fetch('http://localhost:8080/brands/allbrand');
        const brandData = await brandResponse.json();
        setBrands(brandData);

        const chainResponse = await fetch('http://localhost:8080/chains/allcompany');
        const chainData = await chainResponse.json();
        setChains(chainData);

        const zoneResponse = await fetch('http://localhost:8080/zones/allzones');
        const zoneData = await zoneResponse.json();
        setZones(zoneData);

        // Fetch estimates data
        const estimateResponse = await fetch('http://localhost:8080/estimate/all');
        const estimateData = await estimateResponse.json();
        setEstimates(estimateData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="container py-4">
        <div className="text-center mb-4">
          <h2 className="section-header">Estimate Management</h2>
        </div>

        <div className="row mb-4">
          <div className="col-md-4">
            <div className="neon-card">
              <h5>Total Estimate</h5>
              <h2>{estimates.length}</h2> {/* Display the number of fetched estimates */}
            </div>
          </div>
          <div className="col-md-4">
            <select className="form-select neon-select" aria-label="Filter by Group">
              <option selected disabled>Filter by Group</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select className="form-select neon-select" aria-label="Filter by Brand">
              <option selected disabled>Filter by Brand</option>
              {brands.map((brand, index) => (
                <option key={index} value={brand}>{brand}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="d-flex justify-content-end mb-3">
          <span
            className="create-estimate"
            style={{ cursor: 'pointer' }}
            data-bs-toggle="offcanvas"
            data-bs-target="#createPanel"
          >
            <i className="bi bi-plus-circle"></i>
            Create Estimate
          </span>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped align-middle">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group</th>
                <th>Chain ID</th>
                <th>Brand</th>
                <th>Zone</th>
                <th>Service Details</th>
                <th>Total Units</th>
                <th>Price Per Unit</th>
                <th>Total</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {estimates.map((estimate, index) => (
                <tr key={estimate.estimateId}>
                  <td>{index + 1}</td>
                  <td>{estimate.groupName}</td>
                  <td>{estimate.chain.chain_id}</td>
                  <td>{estimate.brandName}</td>
                  <td>{estimate.zoneName}</td>
                  <td>{estimate.service}</td>
                  <td>{estimate.quantity}</td>
                  <td>{estimate.costPerUnit}</td>
                  <td>{estimate.totalCost}</td>
                  <td className="table-action">
                    <i className="bi bi-pencil-square" data-bs-toggle="offcanvas" data-bs-target="#editPanel"></i>
                  </td>
                  <td className="table-action">
                    <i className="bi bi-trash"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Create Estimate Panel */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="createPanel">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Create Estimate</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form>
            <label>Select Group:</label>
            <select className="form-select">
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>

            <label>Select Chain ID or Company Name:</label>
            <select className="form-select">
              {chains.map((chain, index) => (
                <option key={index} value={chain}>{chain}</option>
              ))}
            </select>

            <label>Select Brand:</label>
            <select className="form-select">
              {brands.map((brand, index) => (
                <option key={index} value={brand}>{brand}</option>
              ))}
            </select>

            <label>Select Zone:</label>
            <select className="form-select">
              {zones.map((zone, index) => (
                <option key={index} value={zone}>{zone}</option>
              ))}
            </select>

            <label>Service Provided:</label>
            <input type="text" className="form-control" placeholder="Enter Service" />

            <label>Total Quantity:</label>
            <input type="number" className="form-control" placeholder="Enter Total Qty" />

            <label>Cost Per Quantity:</label>
            <input type="number" className="form-control" placeholder="Enter Cost Per Qty" />

            <label>Estimated Amount in Rs:</label>
            <input type="number" className="form-control" placeholder="Enter Amount" />

            <label>Expected Delivery Date:</label>
            <input type="date" className="form-control" />

            <label>Other Delivery Details:</label>
            <textarea className="form-control" rows="3"></textarea>

            <button className="btn glow-btn mt-3 w-100">Save Estimate</button>
          </form>
        </div>
      </div>

      {/* Edit Estimate Panel */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="editPanel">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Edit Estimate</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form>
            <label>Select Group:</label>
            <select className="form-select">
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>

            <label>Select Chain ID or Company Name:</label>
            <select className="form-select">
              {chains.map((chain, index) => (
                <option key={index} value={chain}>{chain}</option>
              ))}
            </select>

            <label>Select Brand:</label>
            <select className="form-select">
              {brands.map((brand, index) => (
                <option key={index} value={brand}>{brand}</option>
              ))}
            </select>

            <label>Select Zone:</label>
            <select className="form-select">
              {zones.map((zone, index) => (
                <option key={index} value={zone}>{zone}</option>
              ))}
            </select>

            <label>Service Provided:</label>
            <input type="text" className="form-control" value="Propeller Shaft Maintenance" />

            <label>Total Quantity:</label>
            <input type="number" className="form-control" value="10" />

            <label>Cost Per Quantity:</label>
            <input type="number" className="form-control" value="500" />

            <label>Estimated Amount in Rs:</label>
            <input type="number" className="form-control" value="5000" />

            <label>Expected Delivery Date:</label>
            <input type="date" className="form-control" value="2024-12-14" />

            <label>Other Delivery Details:</label>
            <textarea className="form-control" rows="3">Service must be delivered to A-20, 2nd Tower...</textarea>

            <button className="btn glow-btn mt-3 w-100">Update Estimate</button>
          </form>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ManageEstimate;
