import React, { useState, useEffect } from 'react';
import './ManageChains.css';
import Navbar from './Navbar';
import Footer from './Footer';

const ManageChains = () => {
  const [showAddPanel, setShowAddPanel] = useState(false);
  const [showEditPanel, setShowEditPanel] = useState(false);
  const [groups, setGroups] = useState([]);
  const [chains, setChains] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState('All');
  
  // States for form inputs
  const [companyName, setCompanyName] = useState('');
  const [gstNo, setGstNo] = useState('');
  const [groupName, setGroupName] = useState('');

  const toggleAddPanel = () => setShowAddPanel(!showAddPanel);
  const toggleEditPanel = () => setShowEditPanel(!showEditPanel);

  useEffect(() => {
    fetch('http://localhost:8080/groups/allgroups')
      .then((response) => response.json())
      .then((data) => setGroups(data))
      .catch((error) => console.error("Error fetching groups:", error));

    fetch('http://localhost:8080/chains/all')
      .then((response) => response.json())
      .then((data) => setChains(data))
      .catch((error) => console.error("Error fetching chains:", error));
  }, []);

  const handleGroupFilter = (groupName) => {
    setSelectedGroup(groupName);
  };

  const filteredChains = selectedGroup === 'All'
    ? chains
    : chains.filter(chain => chain.group.group_name === selectedGroup);

  // Handle form submission and API call
  const handleAddCompany = (e) => {
    e.preventDefault();
    
    // Construct the API URL with the form data
    const apiUrl = `http://localhost:8080/chains/add?companyName=${companyName}&gstNo=${gstNo}&groupName=${groupName}`;
    
    // Make the API call
    fetch(apiUrl)
      .then((response) => response.text()) // Since response is plain text
      .then((message) => {
        alert(message); // Show the response message in an alert
        toggleAddPanel(); // Close the Add Panel
        // Optionally, refetch chains or update UI based on the response
      })
      .catch((error) => {
        alert("Error adding company: " + error.message); // Show error message
      });
  };

  return (
    <>
      <Navbar />
      <div className="container my-4">
        <h3 className="mb-4 text-info">Manage Chain Section</h3>

        <div className="row g-4 mb-4">
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Groups</h5>
              <h2>3</h2>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Chains</h5>
              <h2>{filteredChains.length}</h2>
            </div>
          </div>
        </div>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <div className="d-flex gap-2">
            <button className="btn btn-neon" onClick={toggleAddPanel}>
              <i className="bi bi-plus-circle"></i> Add Company
            </button>
            <div className="dropdown">
              <button className="btn btn-neon dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                <i className="bi bi-funnel-fill"></i> Filter by Group
              </button>
              <ul className="dropdown-menu">
                <li><a className="dropdown-item" href="#" onClick={() => handleGroupFilter('All')}>All</a></li>
                {groups.map((group, index) => (
                  <li key={index}>
                    <a className="dropdown-item" href="#" onClick={() => handleGroupFilter(group)}>{group}</a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-hover">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group Name</th>
                <th>Company</th>
                <th>GSTN</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredChains.map((chain, index) => (
                <tr key={chain.chain_id}>
                  <td>{index + 1}</td>
                  <td>{chain.group.group_name}</td>
                  <td>{chain.company_name}</td>
                  <td>{chain.gst_no}</td>
                  <td>
                    <i className="bi bi-pencil-square action-icon" onClick={toggleEditPanel}></i>
                    <i className="bi bi-trash action-icon"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Add Company Panel */}
      <div id="slidePanel" className={`slide-panel ${showAddPanel ? 'active' : ''}`}>
        <button className="close-panel" onClick={toggleAddPanel}>&times;</button>
        <h5 className="mb-4">Add Company</h5>
        <form onSubmit={handleAddCompany}>
          <div className="mb-3">
            <label className="form-label text-info">Enter Company Name</label>
            <input
              type="text"
              className="form-control bg-dark text-light border-info"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Enter GSTN Number</label>
            <input
              type="text"
              className="form-control bg-dark text-light border-info"
              value={gstNo}
              onChange={(e) => setGstNo(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Select Group</label>
            <select
              className="form-select bg-dark text-light border-info"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
            >
              <option selected disabled>Select Group</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-neon w-100">Submit</button>
        </form>
      </div>

      {/* Edit Company Panel */}
      <div id="editPanel" className={`slide-panel ${showEditPanel ? 'active' : ''}`}>
        <button className="close-panel" onClick={toggleEditPanel}>&times;</button>
        <h5 className="mb-4">Edit Company</h5>
        <form>
          <div className="mb-3">
            <label className="form-label text-info">Enter Company Name</label>
            <input type="text" className="form-control bg-dark text-light border-info" />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Enter GSTN Number</label>
            <input type="text" className="form-control bg-dark text-light border-info" />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Select Group</label>
            <select className="form-select bg-dark text-light border-info">
              <option selected disabled>Select Group</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-neon w-100">Update</button>
        </form>
      </div>

      <Footer />
    </>
  );
};

export default ManageChains;
