import React, { useEffect, useState } from 'react';
import './ManageEstimate.css';
import Navbar from './Navbar';
import Footer from './Footer';
import axios from 'axios';

const ManageEstimate = () => {
  const [estimates, setEstimates] = useState([]);
  const [filteredEstimates, setFilteredEstimates] = useState([]);
  const [groups, setGroups] = useState([]);
  const [brands, setBrands] = useState([]);
  const [chains, setChains] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');

  // Form fields
  const [formData, setFormData] = useState({
    groupName: '',
    chainId: '',
    brandName: '',
    zoneName: '',
    service: '',
    quantity: '',
    costPerUnit: '',
    deliveryDate: '',
    deliveryDetails: '',
  });

  useEffect(() => {
    axios.get('http://localhost:8080/estimate/all')
      .then(response => {
        setEstimates(response.data);
        setFilteredEstimates(response.data);
      })
      .catch(error => {
        console.error('Error fetching estimates:', error);
      });

    axios.get('http://localhost:8080/groups/allgroups')
      .then(res => setGroups(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:8080/brands/allbrand')
      .then(res => setBrands(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:8080/chains/allcompany')
      .then(res => setChains(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:8080/zones/allzones')
      .then(res => setZones(res.data))
      .catch(err => console.error(err));
  }, []);

  useEffect(() => {
    let filtered = estimates;

    if (selectedGroup) {
      filtered = filtered.filter(e => e.chain.group.group_name === selectedGroup);
    }

    if (selectedBrand) {
      filtered = filtered.filter(e => e.brandName === selectedBrand);
    }

    setFilteredEstimates(filtered);
  }, [selectedGroup, selectedBrand, estimates]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8080/estimate/add', formData)
      .then(response => {
        alert(response.data);
        const offCanvas = document.querySelector('#createPanel');
        const bsOffCanvas = bootstrap.Offcanvas.getInstance(offCanvas);
        bsOffCanvas.hide();
        window.location.reload();
      })
      .catch(error => {
        console.error('Error submitting estimate:', error);
        alert('Failed to save estimate');
      });
  };

  return (
    <div>
      <Navbar />

      <div className="container py-4">
        <h2 className="text-center neon-title mb-4">ESTIMATE MANAGEMENT</h2>

        <div className="row mb-4">
          <div className="col-md-4">
            <div className="neon-card hover-glow">
              <h5>Total Estimate</h5>
              <h2>{filteredEstimates.length}</h2>
            </div>
          </div>
          <div className="col-md-4">
            <select
              className="form-select neon-select"
              value={selectedGroup}
              onChange={(e) => setSelectedGroup(e.target.value)}
            >
              <option value="">Filter by Group</option>
              {groups.map((group, idx) => (
                <option key={idx} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select neon-select"
              value={selectedBrand}
              onChange={(e) => setSelectedBrand(e.target.value)}
            >
              <option value="">Filter by Brand</option>
              {brands.map((brand, idx) => (
                <option key={idx} value={brand}>{brand}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="d-flex justify-content-end align-items-center mb-3">
          <span
            className="create-estimate-text text-info"
            role="button"
            data-bs-toggle="offcanvas"
            data-bs-target="#createPanel"
          >
            <i className="bi bi-plus-circle me-2"></i> Create Estimate
          </span>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped align-middle">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group</th>
                <th>Chain ID</th>
                <th>Brand</th>
                <th>Zone</th>
                <th>Service Details</th>
                <th>Total Units</th>
                <th>Price Per Unit</th>
                <th>Total</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {filteredEstimates.map((estimate, index) => (
                <tr key={estimate.estimateId}>
                  <td>{index + 1}</td>
                  <td>{estimate.chain.group.group_name}</td>
                  <td>{estimate.chain.chain_id}</td>
                  <td>{estimate.brandName}</td>
                  <td>{estimate.zoneName}</td>
                  <td>{estimate.service}</td>
                  <td>{estimate.quantity}</td>
                  <td>{estimate.costPerUnit}</td>
                  <td>{estimate.totalCost}</td>
                  <td className="table-action">
                    <i className="bi bi-pencil-square" data-bs-toggle="offcanvas" data-bs-target="#editPanel"></i>
                  </td>
                  <td className="table-action">
                    <i className="bi bi-trash"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Create Panel */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="createPanel">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Create Estimate</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form onSubmit={handleSubmit}>
            <label>Select Group:</label>
            <select className="form-select" name="groupName" onChange={handleInputChange} required>
              <option value="">Select Group</option>
              {groups.map((group, idx) => (
                <option key={idx} value={group}>{group}</option>
              ))}
            </select>

            <label>Select Chain ID or Company Name:</label>
            <select className="form-select" name="chainId" onChange={handleInputChange} required>
              <option value="">Select Chain</option>
              {chains.map((chain, idx) => (
                <option key={idx} value={chain.chain_id}>{chain.chain_name}</option>
              ))}
            </select>

            <label>Select Brand:</label>
            <select className="form-select" name="brandName" onChange={handleInputChange} required>
              <option value="">Select Brand</option>
              {brands.map((brand, idx) => (
                <option key={idx} value={brand}>{brand}</option>
              ))}
            </select>

            <label>Select Zone:</label>
            <select className="form-select" name="zoneName" onChange={handleInputChange} required>
              <option value="">Select Zone</option>
              {zones.map((zone, idx) => (
                <option key={idx} value={zone}>{zone}</option>
              ))}
            </select>

            <label>Service Provided:</label>
            <input type="text" className="form-control" name="service" onChange={handleInputChange} required />

            <label>Total Quantity:</label>
            <input type="number" className="form-control" name="quantity" onChange={handleInputChange} required />

            <label>Cost Per Quantity:</label>
            <input type="number" className="form-control" name="costPerUnit" onChange={handleInputChange} required />

            <label>Estimated Amount in Rs:</label>
            <input type="number" className="form-control" disabled placeholder="Calculated by backend" />

            <label>Expected Delivery Date:</label>
            <input type="date" className="form-control" name="deliveryDate" onChange={handleInputChange} required />

            <label>Other Delivery Details:</label>
            <textarea className="form-control" name="deliveryDetails" rows="3" onChange={handleInputChange}></textarea>

            <button type="submit" className="btn glow-btn mt-3 w-100">Save Estimate</button>
          </form>
        </div>
      </div>

      {/* Edit Panel (unchanged) */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="editPanel">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Edit Estimate</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form>
            <label>Select Group:</label>
            <select className="form-select">
              {groups.map((group, idx) => (
                <option key={idx}>{group}</option>
              ))}
            </select>

            <label>Select Chain ID or Company Name:</label>
            <select className="form-select">
              {chains.map((chain, idx) => (
                <option key={idx}>{chain}</option>
              ))}
            </select>

            <label>Select Brand:</label>
            <select className="form-select">
              {brands.map((brand, idx) => (
                <option key={idx}>{brand}</option>
              ))}
            </select>

            <label>Select Zone:</label>
            <select className="form-select">
              {zones.map((zone, idx) => (
                <option key={idx}>{zone}</option>
              ))}
            </select>

            <label>Service Provided:</label>
            <input type="text" className="form-control" defaultValue="Propeller Shaft Maintenance" />

            <label>Total Quantity:</label>
            <input type="number" className="form-control" defaultValue="10" />

            <label>Cost Per Quantity:</label>
            <input type="number" className="form-control" defaultValue="500" />

            <label>Estimated Amount in Rs:</label>
            <input type="number" className="form-control" defaultValue="5000" />

            <label>Expected Delivery Date:</label>
            <input type="date" className="form-control" defaultValue="2024-12-14" />

            <label>Other Delivery Details:</label>
            <textarea className="form-control" rows="3">Service must be delivered to A-20, 2nd Tower...</textarea>

            <button className="btn glow-btn mt-3 w-100">Update Estimate</button>
          </form>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ManageEstimate;
