import React, { useState, useEffect } from 'react';
import './ManageChains.css';

const ManageChains = () => {
  const [isPanelActive, setIsPanelActive] = useState(false);
  const [companyName, setCompanyName] = useState('');
  const [gstNo, setGstNo] = useState('');
  const [group, setGroup] = useState('');
  const [chains, setChains] = useState([]); // To store the chains data
  const [groups, setGroups] = useState([]); // To store the groups data from API

  const togglePanel = () => {
    setIsPanelActive(!isPanelActive);
  };

  const handleCompanyNameChange = (e) => setCompanyName(e.target.value);
  const handleGstNoChange = (e) => setGstNo(e.target.value);
  const handleGroupChange = (e) => setGroup(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();

    const companyData = {
      company_name: companyName,
      gst_no: gstNo,
      group: group,
    };

    fetch('http://localhost:8080/chains/add', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(companyData),
    })
      .then((response) => response.text()) // Use .text() if the response is plain text
      .then((data) => {
        alert(data); // Show the plain text response in an alert
        if (data.includes('success')) { // Optional: Check if the response contains a success message
          setIsPanelActive(false); // Close the panel after success
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Error adding company.');
      });
  };

  useEffect(() => {
    // Fetch the groups for the dropdown
    fetch('http://localhost:8080/groups/allgroups')
      .then((response) => response.json()) // Parse the JSON response
      .then((data) => setGroups(data)) // Set the groups data
      .catch((error) => console.error('Error fetching groups:', error));

    // Fetch the chains data
    fetch('http://localhost:8080/chains/all')
      .then((response) => response.json()) // Parse the JSON response
      .then((data) => setChains(data)) // Set the chains data
      .catch((error) => console.error('Error fetching chains:', error));
  }, []); // Empty dependency array means this effect runs once when the component mounts

  return (
    <>
      <nav className="navbar navbar-expand-lg px-3">
        <a className="navbar-brand" href="#">Invoice</a>
        <div className="ms-auto d-flex align-items-center">
          <span className="me-3">Hi User</span>
          <a className="nav-link" href="#"><i className="bi bi-box-arrow-right"></i> Logout</a>
        </div>
      </nav>

      <div className="container my-4">
        <h3 className="mb-4 text-info">Manage Chain Section</h3>

        <div className="row g-4 mb-4">
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Groups</h5>
              <h2>3</h2>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Chains</h5>
              <h2>{chains.length}</h2> {/* Dynamically showing the total number of chains */}
            </div>
          </div>
        </div>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <div className="d-flex gap-2">
            <button className="btn btn-neon" onClick={togglePanel}><i className="bi bi-plus-circle"></i> Add Company</button>
            <div className="dropdown">
              <button className="btn btn-neon dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                <i className="bi bi-funnel-fill"></i> Filter by Group
              </button>
              <ul className="dropdown-menu">
                {groups.map((group, index) => (
                  <li key={index}><a className="dropdown-item" href="#">{group}</a></li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-hover">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group Name</th>
                <th>Company</th>
                <th>GSTN</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {chains.map((chain, index) => (
                <tr key={chain.chain_id}>
                  <td>{index + 1}</td>
                  <td>{chain.group.group_name}</td>
                  <td>{chain.company_name}</td>
                  <td>{chain.gst_no}</td>
                  <td>
                    <i className="bi bi-pencil-square action-icon" onClick={togglePanel}></i>
                    <i className="bi bi-trash action-icon"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Add Company Panel */}
      <div id="slidePanel" className={`slide-panel ${isPanelActive ? 'active' : ''}`}>
        <button className="close-panel" onClick={togglePanel}>&times;</button>
        <h5 className="mb-4">Add Company</h5>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label text-info">Enter Company Name</label>
            <input 
              type="text" 
              className="form-control bg-dark text-light border-info" 
              value={companyName} 
              onChange={handleCompanyNameChange}
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Enter GSTN Number</label>
            <input 
              type="text" 
              className="form-control bg-dark text-light border-info" 
              value={gstNo} 
              onChange={handleGstNoChange}
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Select Group</label>
            <select 
              className="form-select bg-dark text-light border-info" 
              value={group} 
              onChange={handleGroupChange}
            >
              <option selected disabled>Select Group</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-neon w-100">Submit</button>
        </form>
      </div>
    </>
  );
};

export default ManageChains;
