import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Footer from './Footer';
import Navbar from './Navbar';
import './ManageChains.css';

const ManageChains = () => {
  const [chains, setChains] = useState([]);
  const [groups, setGroups] = useState([]);
  const [companyName, setCompanyName] = useState('');
  const [gstNo, setGstNo] = useState('');
  const [groupName, setGroupName] = useState('');
  const [filteredChains, setFilteredChains] = useState([]);
  const [editChainId, setEditChainId] = useState(null);

  const togglePanel = () => {
    document.getElementById('slidePanel').classList.toggle('active');
  };

  useEffect(() => {
    axios.get('http://localhost:8080/chains/all')
      .then(res => {
        setChains(res.data);
        setFilteredChains(res.data);
      })
      .catch(err => console.error("Failed to fetch chains", err));

    axios.get('http://localhost:8080/groups/allgroups')
      .then(res => setGroups(res.data))
      .catch(err => console.error("Failed to fetch groups", err));
  }, []);

  const handleGroupFilter = (selectedGroup) => {
    setGroupName(selectedGroup);
    if (selectedGroup === '') {
      setFilteredChains(chains);
    } else {
      const filtered = chains.filter(chain => chain.group.group_name === selectedGroup);
      setFilteredChains(filtered);
    }
  };

  const handleEditChain = (chain) => {
    setEditChainId(chain.chain_id);
    setCompanyName(chain.company_name);
    setGstNo(chain.gst_no);
    setGroupName(chain.group.group_name);
    togglePanel();
  };

  const handleAddChain = (e) => {
    e.preventDefault();

    const url = `http://localhost:8080/chains/add?companyName=${companyName}&gstNo=${gstNo}&groupName=${groupName}`;

    axios.post(url)
      .then(res => {
        const response = res.data || res;
        if (response === 'Group does not exist') {
          throw new Error('The selected group does not exist.');
        } else {
          setChains([...chains, {
            company_name: companyName,
            gst_no: gstNo,
            group: { group_name: groupName }
          }]);
          togglePanel();
        }
      })
      .catch(err => {
        console.error('Error adding chain:', err.message);
        alert(`Error: ${err.message}`);
      });
  };

  const handleUpdateChain = (e) => {
    e.preventDefault();

    const url = `http://localhost:8080/chains/update`;

    const updatedChain = {
      chainId: editChainId,
      companyName: companyName,
      gstNo: gstNo,
      groupName: groupName
    };

    axios.patch(url, updatedChain)
      .then(res => {
        const response = res.data || res;
        if (typeof response === 'string' && response === 'Group does not exist') {
          throw new Error('The selected group does not exist.');
        }

        setChains(chains.map(chain =>
          chain.chain_id === editChainId
            ? { ...chain, company_name: companyName, gst_no: gstNo, group: { group_name: groupName } }
            : chain
        ));
        togglePanel();
      })
      .catch(err => {
        console.error('Error updating chain:', err.message);
        alert(`Error: ${err.message}`);
      });
  };

  return (
    <div>
      <Navbar />

      <div className="container my-4">
        <h3 className="mb-4 text-info">Manage Chain Section</h3>

        <div className="row g-4 mb-4">
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Groups</h5>
              <h2>{[...new Set(chains.map(chain => chain.group.group_name))].length}</h2>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Chains</h5>
              <h2>{filteredChains.length}</h2>
            </div>
          </div>
        </div>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <div className="d-flex gap-2">
            <button className="btn btn-neon" onClick={togglePanel}>
              <i className="bi bi-plus-circle"></i> Add Company
            </button>
            <div className="dropdown">
              <button className="btn btn-neon dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                <i className="bi bi-funnel-fill"></i> Filter by Group
              </button>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#" onClick={() => handleGroupFilter('')}>
                    All Groups
                  </a>
                </li>
                {groups.map((group, index) => (
                  <li key={index}>
                    <a className="dropdown-item" href="#" onClick={() => handleGroupFilter(group)}>
                      {group}
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-hover">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group Name</th>
                <th>Company</th>
                <th>GSTN</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredChains.map((chain, index) => (
                <tr key={chain.chain_id}>
                  <td>{index + 1}</td>
                  <td>{chain.group.group_name}</td>
                  <td>{chain.company_name}</td>
                  <td>{chain.gst_no}</td>
                  <td>
                    <i className="bi bi-pencil-square action-icon" onClick={() => handleEditChain(chain)}></i>
                    <i className="bi bi-trash action-icon"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <Footer />
      </div>

      <div id="slidePanel" className="slide-panel">
        <button className="close-panel" onClick={togglePanel}>&times;</button>
        <h5 className="mb-4">{editChainId ? 'Edit Company' : 'Add Company'}</h5>
        <form onSubmit={editChainId ? handleUpdateChain : handleAddChain}>
          <div className="mb-3">
            <label className="form-label text-info">Enter Company Name</label>
            <input
              type="text"
              className="form-control bg-dark text-light border-info"
              value={companyName}
              onChange={(e) => setCompanyName(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Enter GSTN Number</label>
            <input
              type="text"
              className="form-control bg-dark text-light border-info"
              value={gstNo}
              onChange={(e) => setGstNo(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Select Group</label>
            <select
              className="form-select bg-dark text-light border-info"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              required
            >
              <option selected disabled>Select Group</option>
              <option value="">All Groups</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>{group}</option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-neon w-100">{editChainId ? 'Update' : 'Submit'}</button>
        </form>
      </div>
    </div>
  );
};

export default ManageChains;
