import React, { useEffect, useState } from 'react';
import Footer from './Footer';
import Navbar from './Navbar';
import './ManageChains.css';

const ManageChains = () => {
  const [chains, setChains] = useState([]);
  const [groups, setGroups] = useState([]); // Added state to store groups
  const [companyName, setCompanyName] = useState('');
  const [gstNo, setGstNo] = useState('');
  const [groupName, setGroupName] = useState('');
  const [filteredChains, setFilteredChains] = useState([]); // State for filtered chains

  const togglePanel = () => {
    document.getElementById('slidePanel').classList.toggle('active');
  };

  // Fetching chains and groups from respective APIs
  useEffect(() => {
    // Fetch chains
    fetch('http://localhost:8080/chains/all')
      .then(res => res.json())
      .then(data => setChains(data))
      .catch(err => console.error("Failed to fetch chains", err));

    // Fetch groups
    fetch('http://localhost:8080/groups/allgroups')
      .then(res => res.json())
      .then(data => {
        console.log(data); // Log the data to check its structure
        setGroups(data);
      })
      .catch(err => console.error("Failed to fetch groups", err));
  }, []);

  // Handle group filter selection
  const handleGroupFilter = (selectedGroup) => {
    setGroupName(selectedGroup); // Set the selected group
    if (selectedGroup === '') {
      setFilteredChains(chains); // Show all chains if no group is selected
    } else {
      const filtered = chains.filter(chain => chain.group.group_name === selectedGroup);
      setFilteredChains(filtered); // Set filtered chains based on selected group
    }
  };

  const handleAddChain = (e) => {
    e.preventDefault();

    const url = `http://localhost:8080/chains/add?companyName=${companyName}&gstNo=${gstNo}&groupName=${groupName}`;

    fetch(url, {
      method: 'POST',
    })
      .then(res => res.text()) // Read the response as text (plain string)
      .then(response => {
        // Check if the response contains the "Group does not exist" message
        if (response === 'Group does not exist') {
          throw new Error('The selected group does not exist.');
        } else {
          // If the response is not the error message, assume success
          console.log('Chain added:', response);
          // Assuming the response contains the new chain's details
          setChains([...chains, { company_name: companyName, gst_no: gstNo, group: { group_name: groupName } }]);
          togglePanel(); // Close the panel after submission
        }
      })
      .catch(err => {
        console.error('Error adding chain:', err.message);
        alert(`Error: ${err.message}`); // Show an alert with the error message
      });
  };

  return (
    <div>
      <Navbar />

      <div className="container my-4">
        <h3 className="mb-4 text-info">Manage Chain Section</h3>

        <div className="row g-4 mb-4">
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Groups</h5>
              <h2>{[...new Set(chains.map(chain => chain.group.group_name))].length}</h2>
            </div>
          </div>
          <div className="col-md-3">
            <div className="card card-stats p-3">
              <h5>Total Chains</h5>
              <h2>{filteredChains.length}</h2> {/* Display filtered chains count */}
            </div>
          </div>
        </div>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <div className="d-flex gap-2">
            <button className="btn btn-neon" onClick={togglePanel}>
              <i className="bi bi-plus-circle"></i> Add Company
            </button>
            <div className="dropdown">
              <button className="btn btn-neon dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                <i className="bi bi-funnel-fill"></i> Filter by Group
              </button>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#" onClick={() => handleGroupFilter('')}>
                    All Groups
                  </a>
                </li>
                {groups.length > 0 ? (
                  groups.map((group, index) => (
                    <li key={index}>
                      <a className="dropdown-item" href="#" onClick={() => handleGroupFilter(group)}>
                        {group}
                      </a>
                    </li>
                  ))
                ) : (
                  <li><a className="dropdown-item" href="#">No groups available</a></li>
                )}
              </ul>
            </div>
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-hover">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group Name</th>
                <th>Company</th>
                <th>GSTN</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredChains.length > 0 ? (
                filteredChains.map((chain, index) => (
                  <tr key={chain.chain_id}>
                    <td>{index + 1}</td>
                    <td>{chain.group.group_name}</td>
                    <td>{chain.company_name}</td>
                    <td>{chain.gst_no}</td>
                    <td>
                      <i className="bi bi-pencil-square action-icon"></i>
                      <i className="bi bi-trash action-icon"></i>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="text-center">No chains found for the selected group.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <Footer />
      </div>

      <div id="slidePanel" className="slide-panel">
        <button className="close-panel" onClick={togglePanel}>&times;</button>
        <h5 className="mb-4">Add Company</h5>
        <form onSubmit={handleAddChain}>
          <div className="mb-3">
            <label className="form-label text-info">Enter Company Name</label>
            <input 
              type="text" 
              className="form-control bg-dark text-light border-info" 
              value={companyName} 
              onChange={(e) => setCompanyName(e.target.value)} 
              required 
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Enter GSTN Number</label>
            <input 
              type="text" 
              className="form-control bg-dark text-light border-info" 
              value={gstNo} 
              onChange={(e) => setGstNo(e.target.value)} 
              required 
            />
          </div>
          <div className="mb-3">
            <label className="form-label text-info">Select Group</label>
            <select 
              className="form-select bg-dark text-light border-info"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)} 
              required
            >
              <option selected disabled>Select Group</option>
              {groups.length > 0 ? (
                groups.map((group, index) => (
                  <option key={index} value={group}>{group}</option>
                ))
              ) : (
                <option disabled>No groups available</option>
              )}
            </select>
          </div>
          <button type="submit" className="btn btn-neon w-100">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default ManageChains;
