import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ManageGroup.css';
import Navbar from './Navbar';
import Footer from './Footer';

const ManageGroup = () => {
  const [groups, setGroups] = useState([]);
  const [newGroupName, setNewGroupName] = useState('');
  const [editedGroupName, setEditedGroupName] = useState(''); // State to hold the group name being edited
  const [isEditing, setIsEditing] = useState(null); // Track which group is being edited

  // Fetch all groups
  const fetchGroups = async () => {
    try {
      const response = await axios.get('http://localhost:8080/groups/all');
      setGroups(response.data);
    } catch (error) {
      console.error('Error fetching groups:', error.response?.data || error.message || error);
    }
  };

  // Add new group
  const handleAddGroup = async () => {
    if (!newGroupName.trim()) return;

    try {
      await axios.post('http://localhost:8080/groups/addgroup', {
        groupName: newGroupName,
      });
      setNewGroupName('');
      fetchGroups(); // Refresh group list
    } catch (error) {
      console.error('Error adding group:', error.response?.data || error.message || error);
    }
  };

  // Handle Edit (switch between edit and save)
  const handleEdit = (groupId, groupName) => {
    setIsEditing(groupId); // Set the group as being edited
    setEditedGroupName(groupName); // Pre-fill the current group name into the input
  };

  // Handle Save (save the new group name using PATCH)
  const handleSave = async (groupId) => {
    try {
      const response = await axios.patch(`http://localhost:8080/groups/update/${groupId}`, {
        groupName: editedGroupName, // Send the updated name in the request body
      });
      alert(response.data.message); // Show the response message from the backend
      setIsEditing(null); // Exit edit mode
      fetchGroups(); // Refresh the group list
    } catch (error) {
      console.error('Error saving group:', error.response?.data || error.message || error);
    }
  };

  useEffect(() => {
    fetchGroups();
  }, []);

  return (
    <div className='MG'>
      <Navbar />

      <div className="total-groups">Total Groups: {groups.length}</div>

      <div className="add-group-container">
        <h2 className="add-group-title">Add a New Group</h2>
        <input
          type="text"
          className="add-group-input"
          placeholder="Enter group name"
          value={newGroupName}
          onChange={(e) => setNewGroupName(e.target.value)}
        />
        <button className="add-group-btn" onClick={handleAddGroup}>
          <i className="fas fa-plus"></i>
        </button>
      </div>

      <div className="container">
        <table className="table table-dark table-bordered">
          <thead>
            <tr>
              <th>#</th>
              <th>Group Name</th>
              <th>Date Created</th>
              <th>Updated At</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {groups.map((group, index) => (
              <tr key={group.group_id || index}>
                <td>{index + 1}</td>
                <td>
                  {isEditing === group.group_id ? (
                    <input
                      type="text"
                      value={editedGroupName}
                      onChange={(e) => setEditedGroupName(e.target.value)} // Update the input field value
                    />
                  ) : (
                    group.groupName
                  )}
                </td>
                <td>{group.created_at?.split('T')[0] || 'N/A'}</td>
                <td>{group.updated_at?.split('T')[0] || 'N/A'}</td>
                <td>
                  {group.is_active ? (
                    <i className="fas fa-check-circle" style={{ color: 'green' }}></i>
                  ) : (
                    <i className="fas fa-times-circle" style={{ color: 'red' }}></i>
                  )}
                </td>
                <td className="action-icons">
                  {isEditing === group.group_id ? (
                    <i
                      className="fas fa-arrow-right"
                      onClick={() => handleSave(group.group_id)} // Save on click of right arrow
                    ></i>
                  ) : (
                    <i
                      className="fas fa-pen-to-square"
                      onClick={() => handleEdit(group.group_id, group.groupName)} // Edit on click of edit icon
                    ></i>
                  )}
                  <i className="fas fa-trash"></i>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Footer />
    </div>
  );
};

export default ManageGroup;
