import React, { useEffect, useState } from 'react';
import './ManageEstimate.css';
import Navbar from './Navbar';
import Footer from './Footer';

const ManageEstimate = () => {
  const [estimates, setEstimates] = useState([]);
  const [groups, setGroups] = useState([]);
  const [brands, setBrands] = useState([]);
  const [chains, setChains] = useState([]);
  const [zones, setZones] = useState([]);
  const [formData, setFormData] = useState({
    groupName: '',
    chainId: '',
    brandName: '',
    zoneName: '',
    service: '',
    quantity: '',
    costPerUnit: '',
    deliveryDate: '',
    deliveryDetails: '',
  });

  useEffect(() => {
    fetch('http://localhost:8080/estimate/all')
      .then((response) => response.json())
      .then((data) => setEstimates(data))
      .catch((error) => console.error('Error fetching estimates:', error));

    fetch('http://localhost:8080/groups/allgroups')
      .then((res) => res.json())
      .then((data) => setGroups(data))
      .catch((err) => console.error('Groups Error:', err));

    fetch('http://localhost:8080/brands/allbrand')
      .then((res) => res.json())
      .then((data) => setBrands(data))
      .catch((err) => console.error('Brands Error:', err));

    fetch('http://localhost:8080/chains/allcompany')
      .then((res) => res.json())
      .then((data) => setChains(data))
      .catch((err) => console.error('Chains Error:', err));

    fetch('http://localhost:8080/zones/allzones')
      .then((res) => res.json())
      .then((data) => setZones(data))
      .catch((err) => console.error('Zones Error:', err));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const estimateData = {
      groupName: formData.groupName,
      chainId: parseInt(formData.chainId), // Ensure it's an integer
      brandName: formData.brandName,
      zoneName: formData.zoneName,
      service: formData.service,
      quantity: formData.quantity,
      costPerUnit: formData.costPerUnit,
      deliveryDate: formData.deliveryDate,
      deliveryDetails: formData.deliveryDetails,
    };

    fetch('http://localhost:8080/estimate/add', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(estimateData),
    })
      .then((response) => response.text())
      .then((text) => {
        alert(text); // Display plain text response in alert
        // Optionally, you can fetch updated estimates after adding
        // fetch('http://localhost:8080/estimate/all')
        //   .then((response) => response.json())
        //   .then((data) => setEstimates(data))
        //   .catch((error) => console.error('Error fetching estimates:', error));
      })
      .catch((error) => {
        console.error('Error adding estimate:', error);
        alert('Error adding estimate');
      });
  };

  return (
    <div>
      <Navbar />
      <div className="container py-4">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="section-header">Estimate Management</h2>
        </div>

        <div className="row mb-4">
          <div className="col-md-4">
            <div className="neon-card">
              <h5>Total Estimate</h5>
              <h2>{estimates.length}</h2>
            </div>
          </div>
          <div className="col-md-4">
            <select
              className="form-select neon-select"
              aria-label="Filter by Group"
              name="groupName"
              value={formData.groupName}
              onChange={handleInputChange}
            >
              <option selected disabled>Filter by Group</option>
              {groups.map((group, index) => (
                <option key={index} value={group}>
                  {group}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md-4">
            <select
              className="form-select neon-select"
              aria-label="Filter by Brand"
              name="brandName"
              value={formData.brandName}
              onChange={handleInputChange}
            >
              <option selected disabled>Filter by Brand</option>
              {brands.map((brand, index) => (
                <option key={index} value={brand}>
                  {brand}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="d-flex justify-content-end mb-3">
          <div
            className="create-estimate-icon"
            data-bs-toggle="offcanvas"
            data-bs-target="#createPanel"
            role="button"
          >
            <i className="bi bi-plus-circle me-2"></i>
            Create Estimate
          </div>
        </div>

        <div className="table-responsive">
          <table className="table table-dark table-striped align-middle">
            <thead>
              <tr>
                <th>Sr.No</th>
                <th>Group</th>
                <th>Chain ID</th>
                <th>Brand</th>
                <th>Zone</th>
                <th>Service Details</th>
                <th>Total Units</th>
                <th>Price Per Unit</th>
                <th>Total</th>
                <th>Edit</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {estimates.map((estimate, index) => (
                <tr key={estimate.estimateId}>
                  <td>{index + 1}</td>
                  <td>{estimate.chain.group.group_name}</td>
                  <td>{estimate.chain.chain_id}</td>
                  <td>{estimate.brandName}</td>
                  <td>{estimate.zoneName}</td>
                  <td>{estimate.service}</td>
                  <td>{estimate.quantity}</td>
                  <td>{estimate.costPerUnit}</td>
                  <td>{estimate.totalCost}</td>
                  <td className="table-action">
                    <i className="bi bi-pencil-square" data-bs-toggle="offcanvas" data-bs-target="#editPanel"></i>
                  </td>
                  <td className="table-action">
                    <i className="bi bi-trash"></i>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Create Estimate Panel */}
      <div className="offcanvas offcanvas-end" tabIndex="-1" id="createPanel">
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Create Estimate</h5>
          <button type="button" className="btn-close btn-close-white" data-bs-dismiss="offcanvas"></button>
        </div>
        <div className="offcanvas-body">
          <form onSubmit={handleSubmit}>
            <label>Select Group:</label>
            <select
              className="form-select"
              name="groupName"
              value={formData.groupName}
              onChange={handleInputChange}
            >
              {groups.map((group, index) => (
                <option key={index} value={group}>
                  {group}
                </option>
              ))}
            </select>

            <label>Select Chain ID or Company Name:</label>
            <select
              className="form-select"
              name="chainId"
              value={formData.chainId}
              onChange={handleInputChange}
            >
              {chains.map((chain, index) => (
                <option key={index} value={chain.chain_id}>
                  {chain.company_name} ({chain.chain_id})
                </option>
              ))}
            </select>

            <label>Select Brand:</label>
            <select
              className="form-select"
              name="brandName"
              value={formData.brandName}
              onChange={handleInputChange}
            >
              {brands.map((brand, index) => (
                <option key={index} value={brand}>
                  {brand}
                </option>
              ))}
            </select>

            <label>Select Zone:</label>
            <select
              className="form-select"
              name="zoneName"
              value={formData.zoneName}
              onChange={handleInputChange}
            >
              {zones.map((zone, index) => (
                <option key={index} value={zone}>
                  {zone}
                </option>
              ))}
            </select>

            <label>Service Provided:</label>
            <input
              type="text"
              className="form-control"
              name="service"
              value={formData.service}
              onChange={handleInputChange}
              placeholder="Enter Service"
            />

            <label>Total Quantity:</label>
            <input
              type="number"
              className="form-control"
              name="quantity"
              value={formData.quantity}
              onChange={handleInputChange}
              placeholder="Enter Total Qty"
            />

            <label>Cost Per Quantity:</label>
            <input
              type="number"
              className="form-control"
              name="costPerUnit"
              value={formData.costPerUnit}
              onChange={handleInputChange}
              placeholder="Enter Cost Per Qty"
            />

            <label>Estimated Amount in Rs:</label>
            <input
              type="number"
              className="form-control"
              name="totalCost"
              value={formData.totalCost}
              onChange={handleInputChange}
              placeholder="Enter Amount" disabled
            />

            <label>Expected Delivery Date:</label>
            <input
              type="date"
              className="form-control"
              name="deliveryDate"
              value={formData.deliveryDate}
              onChange={handleInputChange}
            />

            <label>Other Delivery Details:</label>
            <textarea
              className="form-control"
              name="deliveryDetails"
              value={formData.deliveryDetails}
              onChange={handleInputChange}
              rows="3"
            ></textarea>

            <button className="btn glow-btn mt-3 w-100" type="submit">
              Save Estimate
            </button>
          </form>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ManageEstimate;
